  let(:valid_session) { {} }
  let(:user) { FactoryGirl.create(:user) }

  describe "GET index" do
    it "assigns all chambers as @chambers" do
      chamber = Chamber.create! valid_attributes
      get :index, {}, valid_session
      assigns(:chambers).should eq([chamber])
    end
  end

  describe "GET show" do
    it "assigns the requested chamber as @chamber" do
      chamber = Chamber.create! valid_attributes
      get :show, {:id => chamber.to_param}, valid_session
      assigns(:chamber).should eq(chamber)
    end
  end

  describe "GET new" do
    before do 
      sign_in user, no_capybara: true
    end

    it "assigns a new chamber as @chamber" do
      get :new, {}, valid_session
      assigns(:chamber).should be_a_new(Chamber)
    end
  end

  describe "GET edit" do
    before do 
      sign_in user, no_capybara: true
    end

    it "assigns the requested chamber as @chamber" do
      chamber = Chamber.create! valid_attributes
      get :edit, {:id => chamber.to_param}, valid_session
      assigns(:chamber).should eq(chamber)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      before do 
        sign_in user, no_capybara: true
      end

      it "creates a new Chamber" do
        expect {
          post :create, {:chamber => valid_attributes}, valid_session
        }.to change(Chamber, :count).by(1)
      end

      it "assigns a newly created chamber as @chamber" do
        post :create, {:chamber => valid_attributes}, valid_session
        assigns(:chamber).should be_a(Chamber)
        assigns(:chamber).should be_persisted
      end

      it "redirects to the created chamber" do
        post :create, {:chamber => valid_attributes}, valid_session
        response.should redirect_to(Chamber.last)
      end
    end

    describe "with invalid params" do
      before { sign_in user, no_capybara: true}

      it "assigns a newly created but unsaved chamber as @chamber" do
