        Chamber.any_instance.should_receive(:update).with({ "name" => "MyString" })
        put :update, {:id => chamber.to_param, :chamber => { "name" => "MyString" }}, valid_session
      end

      it "assigns the requested chamber as @chamber" do
        chamber = Chamber.create! valid_attributes
        put :update, {:id => chamber.to_param, :chamber => valid_attributes}, valid_session
